class Solution:
    def canPermutePalindrome(self, s: str) -> bool:
        d = {}
        for letter in s:
            try:
                d[letter] += 1
            except:
                d[letter] = 1
        mark = 0
        for key, value in d.items():
            if value%2 == 1:
                mark += 1
        print(d)
        return mark <= 1
        
