''' #很多e，重复元素
"leeeeetcode"
""
'''


class Solution:
    def isSubsequence(self, s: str, t: str) -> bool:
        if not s:
            return True
        if not t:
            return False
        ind_t = 0
        ind_s = 0
        while ind_t<len(t) and ind_s<len(s):
            if t[ind_t]==s[ind_s]:
                ind_s+=1
            ind_t+=1
        return ind_s==len(s) #检查最后是否能满足所有s中的元素 #因为什么而跳出的循环


